import { useEffect, useState } from 'react'
import { useParams } from "react-router-dom";
import './bookDisplay.css'
import { Link } from 'react-router-dom';


function BookDisplay() {
    const [books, setBooks] = useState([])
    useEffect(() => {
        fetch('https://book-swap-api.dev.io-academy.uk/api/books')
            .then(res => res.json())
            .then(bookData => {
                setBooks(bookData.data)
            })
    }, [])
    return (
        <div>
             <div className="filter-container">
        <label htmlFor="filter">Filter by Genre:</label>
        <select id="filter" value={selectedFilter} onChange={handleFilterChange}>
          <option value="all">All</option>
          <option value="Mystery">Mystery</option>
          <option value="Fantasy">Fantasy</option>
        </select>
      </div>

        <div className='flex_container'>
                {books.map((book, index) => (
                    <Link key={index} to={`/Book/${book.id}`}>
                    <div className='card'>
                      <img src={book.image} alt={book.title} className='book_image' />
                      <p className='book_title'>{book.title}</p>
                      <p className='book_author'>{book.author}</p>
                      <p className='book_genre'>{book.genre.name}</p>
                    </div>
                  </Link>
                ))}
        </div>
        </div>
    )

}

    export default BookDisplay
