import { useState } from "react";
import "./addreview.css";

function AddReviewForm(props) {
  const [name, setName] = useState("");
  const [rating, setRating] = useState("");
  const [review, setReview] = useState("");
  const [nameError, setNameError] = useState("");
  const [ratingError, setRatingError] = useState("");
  const [reviewError, setReviewError] = useState("");
  const [reviewCharCount, setReviewCharCount] = useState(0);
  const [reviews, setReviews] = useState([])
  const maxReviewLength = 450;

  function handleReviewChange(event) {
    const newReview = event.target.value;
    if (newReview.length <= maxReviewLength) {
      setReview(newReview);
      setReviewCharCount(newReview.length);
    }
  }

  function handleSubmit(event) {
    event.preventDefault();
    setNameError(data.message.name);
    setRatingError("");
    setReviewError("");

    let isValid = true;

    if (name.trim() === "") {
      isValid = false;
    }

    if (rating === "") {
      setRatingError("Rating field is required");
      isValid = false;
    }

    if (review.trim() === "") {
      setReviewError("Review field is required");
      isValid = false;
    }

    if (isValid) {
      fetch("https://book-swap-api.dev.io-academy.uk/api/reviews", {
        method: "POST",
        body: JSON.stringify({
          name: `${name}`,
          rating: rating,
          review: `${review}`,
          book_id: id,
        }),

        mode: "cors",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
      })
        .then((res) => res.json())
        .then((data) => {
          console.log(data);
          setName("");
          setRating("");
          setReview("");
          setReviewCharCount(0);
          setReviews((prevReviews) => [
            ...prevReviews,
            {
              name: name,
              rating: rating,
              review: review,
              book_id: id,
            },
          ]);
        });
    }
  }

  function changeName(event) {
    setName(event.target.value);
  }

  const id = props.id;

  return (
    <form className="review-form" onSubmit={handleSubmit}>
      <p className="review-title">Want to review this book?</p>
      <label className="label-names" htmlFor="name">
        Name
      </label>
      <input
        placeholder="Name"
        id="name"
        type="text"
        value={name}
        onChange={changeName}
      />
      <div className="error-message">{nameError}</div>

      <label className="label-names" htmlFor="rating">
        Rating (out of 5)
      </label>
      <input
        id="rating"
        type="number"
        min="1"
        max="5"
        value={rating}
        onChange={(e) => setRating(e.target.value)}
      />
      <div className="error-message">{ratingError}</div>

      <label className="label-names" htmlFor="review">
        Review
      </label>
      <textarea id="review" value={review} onChange={handleReviewChange} />
      <div className="char-count">
        {reviewCharCount} / {maxReviewLength} characters
      </div>
      <div className="error-message">{reviewError}</div>
      <input className="submit" type="submit" value='Review'/>
    </form>
  );
}

export default AddReviewForm;
